mapper.update:
UPDATE tb_content
SET category_id = #{tbContentCategory.id},title = #{title},sub_title = #{subTitle},title_desc = #{titleDesc},url = #{url},pic=#{pic},pic2= #{pic2},
    content=#{content},created = #{created},updated = #{updated}
WHERE id = #{id}

mapper.select:
select

from tb_content c

where c.id = #{id}

mapper.delete:
delete from tb_content
where id in

    #{id}
